import React, { useState } from "react";
import M from 'materialize-css/dist/js/materialize'
import {connect} from 'react-redux'
import {addLog} from '../../redux/actions/logActions'
import {getTechs} from '../../redux/actions/techActions'

const AddLogModal = ({addLog,tech:{techs,loading}}) => {


  const [message, setMessage] = useState("");
  const [attention, setAttention] = useState(false);
  const [tech, setTech] = useState("");

  const handleMessage = (e) => {

    setMessage(e.target.value);
  };

  const handleTech = (e) => {
    setTech(e.target.value);
  };

  const handleAttention = () => {
    setAttention(!attention);
  };

  const onSubmit = (e) =>{
    e.preventDefault();
      if(message === '' || tech === ''){
         M.toast({html:'Please enter a message and tech'})
      }

      const newLog = {
        message: message,
        attention,
        tech,
        date: new Date()
      }

      addLog(newLog)

      M.toast({html:`Log added by ${tech}`})


      setMessage('')
      setTech("")
      setAttention(false)
  }

  return (
    <div id="add-log-modal" className="modal" style={modalStyle}>
      <div className="modal-content">
        <h4>Enter System Log</h4>
        <div className="row">
          <div className="input-field">
            <input
              type="text"
              name="message"
              value={message}
              required={true}
              onChange={handleMessage}
            />
            <label htmlFor="message" className="active">
              Log Message
            </label>
          </div>
        </div>

        <div className="row">
          <div className="input-field">
            <select
              className="browser-default"
              name="tech"
              value={tech}
              onChange={handleTech}

            >
              <option value="" disabled selected>
                Choose Techician
              </option>
              <option value="John Doe">John Doe</option>
              <option value="Sam Smith">Sam Smith</option>
              <option value="Sara Wilson">Sara Wilson</option>
            </select>
          </div>
        </div>

        <div className="row">
          <div className="input-field">
            <p>
              <label>
                <input
                  type="checkbox"
                  className="filled-in"
                  checked={attention}
                  onChange={handleAttention}
                />
                <span>Needs Attention</span>
              </label>
            </p>
          </div>
        </div>

      </div>


      <div className="modal-footer">
          <a href="#!" onClick={onSubmit} className={ !!message ? 'modal-close waves-effect blue btn ' :'modal-close waves-effect blue btn disabled' }>
              Enter
          </a>
      </div>



    </div>
  );
};

const modalStyle = {
  width: "75%",
  height: "75%",
};

export default connect(null,{addLog})(AddLogModal);
