import React from 'react'
import PropTypes from 'prop-types'
import Moment from 'react-moment'
import {connect} from 'react-redux'
import {deleteLog} from '../../redux/actions/logActions'
import {addCompletedLog} from '../../redux/actions/logActions'

import M from 'materialize-css/dist/js/materialize'


const CompletedLogItem = ({log,deleteLog,addCompletedLog}) => {



    // const {id,message,tech,date} = current;



    return (
       <li className="collection-item">
            <div className="center">
                <a href="#edit-log-modal" className={`modal-trigger ${log&&log.attention ? 'red-text' : 'blue-text'}`}>{current&&current.message}</a>
                <br/>
                <span className="grey-text">
                    <span className="black-text">ID #{log&&log.id} </span>
                    completed by {" "}
                    <span className="black-text">{log&&log.tech}</span> 
                   <br/> {" "} <span style={{marginLeft:50}}>on</span> {" "} 
                     <Moment format='MMMM Do YYYY, h:mm:ss a'>{current&&current.date}</Moment>
                </span>

            </div>
       </li>
    )
}

CompletedLogItem.propTypes = {
    log: PropTypes.object.isRequired,  
}

const mapStateToProps = state =>({
    current:state.log.current
  })

export default connect(mapStateToProps,{deleteLog,addCompletedLog})(CompletedLogItem)
