import React, { useState , useEffect } from "react";
import M from 'materialize-css/dist/js/materialize'
import {connect} from 'react-redux'
import PropTypes from 'prop-types'
import {updateTech} from '../../redux/actions/techActions'


const EditTechModal = ({updateTech,current}) => {

  console.log(current)
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState(false);

   useEffect(() => {

     if(current){
       setFirstName(current.firstName)
       setLastName(current.lastName)
     }
   },[current])

  const handleFirstName = (e) => {
    setFirstName(e.target.value);
  };

  const handleLastName = (e) => {
    setLastName(e.target.value);
  };


  const onSubmit = () =>{

      if(firstName === '' || lastName === ''){
         M.toast({html:'Please enter tech details'})
      }else{
        const updatedTech = {
          id:current.id,
          firstName,
          lastName,
          date: new Date()
        }
        updateTech(updatedTech)
        M.toast({html:'Tech Updated'})

      //clear inputs fields
      setFirstName('')
      setLastName("")

      }

  }

  return (
    <div id="edit-tech-modal" className="modal" style={modalStyle}>
      <div className="modal-content">
        <h4>Edit Tech</h4>
        <div className="row">
          <div className="input-field">
            <input
              type="text"
              name="first name"
              value={firstName}
              onChange={handleFirstName}
            />
          </div>
        </div>

        <div className="row">
          <div className="input-field">
            <input
              type="text"
              name="last name"
              value={lastName}
              onChange={handleLastName}
            />
          </div>
        </div>

      </div>


      <div className="modal-footer">
          <a href="#!" onClick={onSubmit} className="modal-close waves-effect blue btn ">
              Send
          </a>
      </div>

    </div>
  );
};

const modalStyle = {
  width: "75%",
  height: "75%",
};

EditTechModal.propTypes = {
current: PropTypes.object,
updateTech:PropTypes.func.isRequired,
}

const mapStateToProps = state =>({
  current:state.log.current
})

export default connect(mapStateToProps,{updateTech})( EditTechModal);
