import React from 'react'
import PropTypes from 'prop-types'
import Moment from 'react-moment'
import {connect} from 'react-redux'
import {deleteLog} from '../../redux/actions/logActions'
import {setCurrentLog} from '../../redux/actions/logActions'
import {clearCurrentLog} from '../../redux/actions/logActions'

import M from 'materialize-css/dist/js/materialize'


const LogItem = ({log,deleteLog,setCurrentLog,clearCurrentLog,updateLog}) => {

    const {id,message,tech,date} = log


    const handleDelete=()=>{
        deleteLog(id)
        M.toast({html:`Log Deleted`})
    }

    const handleUpdateLog=()=>{
        updateLog(log)
        M.toast({html:`Log Updated`})
    }

    const handleCurrentLog=()=>{
        setCurrentLog(log)
    }

    const handleClearCurrentLog=()=>{
        clearCurrentLog()
    }

    return (
       <li className="collection-item">
            <div className="center">
                <a href="#edit-log-modal" className={`modal-trigger ${log.attention ? 'red-text' : 'blue-text'}`} onClick={handleCurrentLog}>{message}</a>
                <br/>
                <span className="grey-text">
                    <span className="black-text">ID #{id} </span>
                    last updated by {" "}
                    <span className="black-text">{tech}</span> 
                    <br/> on {" "} 
                     <Moment format='MMMM Do YYYY, h:mm:ss a'>{date}</Moment>
                </span>
                <a href="#!" className="secondary-content" onClick={handleDelete}>
                    <i className='material-icons grey-text'>delete_forever</i>
                </a>
            </div>
       </li>
    )
}

LogItem.propTypes = {
    log: PropTypes.object.isRequired,
    deleteLog:PropTypes.func.isRequired,
    setCurrentLog: PropTypes.func.isRequired,
    updateLog: PropTypes.func.isRequired,
    clearCurrentLog:PropTypes.func.isRequired
}

export default connect(null,{deleteLog,clearCurrentLog,setCurrentLog,updateLog})(LogItem)
