import {
    GET_TECHS,
    TECHS_ERROR,
    DELETE_TECHS,
    ADD_TECH
} from '../actions/types';


const initialState = {
    techs: null,
    error: null,
    loading:false
}

const techReducer = (state=initialState,action) =>{
   switch(action.type){
      case GET_TECHS :
          return {
              ...state,
              techs: action.payload,
              loading:false
          }
      case ADD_TECH:
          return {}



    default :
       return state;
   }
}

export default techReducer;
